import requestsimport randomfrom bs4 import BeautifulSoupdef sing17():    for i in range(30):        songid  = "%08d" % random.randint(0,1e8)        url = 'http://17sing.tw/share_song/index.html?sid={}'.format(songid)        request = requests.get(url)        ycontent = request.content        soup = BeautifulSoup(ycontent, 'html.parser')        songimage = soup.find("meta",{"property":"og:image"})        img  = songimage.attrs["content"]        songurl = soup.find("meta",{"property":"og:url"})        surl = songurl.attrs["content"]        songtitl = soup.find("meta",{"property":"og:description"})        titl = songtitl.attrs["content"][0:50]        content = '{}\n{}...'.format(surl,titl)                if len(img) > 0:            return content        else:            return "找不到歌 請再試一次!!"